const btnHamburger=document.querySelector(".btn"),navbar=document.querySelector(".navbar__nav__ul"),nav=document.querySelector(".navbar-wrapper"),scrollFromTop=document.querySelector(".navbar-wrapper").offsetHeight,topFooterDiv=document.querySelectorAll(".top-footer__column"),topFooterUl=document.querySelectorAll(".top-footer__column__ul"),topFooterIcon=document.querySelectorAll(".fa-angle-down");btnHamburger.addEventListener("click",()=>{btnHamburger.classList.toggle("btn-active"),btnHamburger.classList.toggle("btn-not-active"),navbar.classList.toggle("navbar-active")}),topFooterDiv.forEach((e,t)=>{e.addEventListener("click",function(){topFooterUl[t].classList.toggle("top-footer-active"),topFooterIcon[t].classList.toggle("top-footer-icon-active")})});const transparentNavbar=()=>{window.pageYOffset>=scrollFromTop?nav.classList.add("navbar-wrapper-active"):window.pageYOffset<scrollFromTop&&nav.classList.remove("navbar-wrapper-active")};window.addEventListener("scroll",transparentNavbar);